mapper.select:
select table_name, table_comment, create_time, update_time from information_schema.tables
where table_comment  <>  '' and table_schema = (select database())
and table_name = #{tableName}

mapper.update:
update gen_table
table_name = #{tableName}, table_comment = #{tableComment}, class_name = #{className}, function_author = #{functionAuthor}, gen_type = #{genType}, gen_path = #{genPath}, tpl_category = #{tplCategory}, package_name = #{packageName}, module_name = #{moduleName}, business_name = #{businessName}, function_name = #{functionName}, options = #{options}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where table_id = #{tableId}
