DROP TABLE IF EXISTS `ai_write`;
CREATE TABLE `ai_write`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '编号',
  `user_id` bigint NOT NULL COMMENT '用户编号',
  `type` int NULL DEFAULT NULL COMMENT '写作类型',
