SELECT owner_user_id, COUNT(1) AS customer_create_count FROM crm_customer WHERE deleted = 0 AND owner_user_id in
#{userId}
AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY owner_user_id

SELECT a.owner_user_id, count( DISTINCT a.id ) AS customer_deal_count FROM crm_customer AS a LEFT JOIN crm_contract AS b ON b.customer_id = a.id WHERE a.deleted = 0 AND b.deleted = 0 AND b.audit_status = 20 AND a.owner_user_id IN
#{userId}
AND b.create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY a.owner_user_id

SELECT owner_user_id, IFNULL(SUM(total_price), 0) AS contract_price FROM crm_contract WHERE deleted = 0 AND audit_status = 20 AND owner_user_id in
#{userId}
AND order_date BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY owner_user_id

SELECT owner_user_id, IFNULL(SUM(price), 0) AS receivable_price FROM crm_receivable WHERE deleted = 0 AND audit_status = 20 AND owner_user_id IN
#{userId}
AND return_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY owner_user_id

SELECT DATE_FORMAT( create_time, #{sqlDateFormat} ) AS time,
count(*) AS followup_record_count
FROM crm_follow_up_record
WHERE creator IN

    #{userId}

AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND
#{times[1],javaType=java.time.LocalDateTime}
AND biz_type = #{bizType}
GROUP BY time

SELECT
DATE_FORMAT( create_time, #{sqlDateFormat} ) AS time,
count(DISTINCT biz_id) AS followup_customer_count
FROM crm_follow_up_record
WHERE creator IN

    #{userId}

AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND
#{times[1],javaType=java.time.LocalDateTime}
AND biz_type = #{bizType}
GROUP BY time

SELECT creator as owner_user_id,
count(*) AS followup_record_count
FROM crm_follow_up_record
WHERE creator IN

    #{userId}

AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND
#{times[1],javaType=java.time.LocalDateTime}
AND biz_type = #{bizType}
GROUP BY creator

SELECT creator as owner_user_id,
count(DISTINCT biz_id) AS followup_customer_count
FROM crm_follow_up_record
WHERE creator IN

    #{userId}

AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND
#{times[1],javaType=java.time.LocalDateTime}
AND biz_type = #{bizType}
GROUP BY creator

SELECT
type AS followupType,
count(*) AS followup_record_count
FROM crm_follow_up_record
WHERE creator IN

    #{userId}

AND create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND
#{times[1],javaType=java.time.LocalDateTime}
AND biz_type = #{bizType}
GROUP BY followupType

SELECT a.`name` AS customer_name, b.`name` AS contract_name, b.total_price, IFNULL( c.price, 0 ) AS receivable_price, a.industry_id, a.source, a.owner_user_id, a.creator AS creator_user_id, a.create_time, b.order_date FROM crm_customer AS a INNER JOIN crm_contract AS b ON a.id = b.customer_id LEFT JOIN crm_receivable AS c ON b.id = c.contract_id WHERE a.deleted = 0 AND b.deleted = 0 AND c.deleted = 0 AND b.audit_status = 20 AND a.owner_user_id IN
#{userId}
AND b.create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime}

SELECT DATE_FORMAT( b.order_date, #{sqlDateFormat} ) AS time, IFNULL( TRUNCATE ( AVG( TIMESTAMPDIFF( DAY, a.create_time, b.order_date )), 1 ), 0 ) AS customer_deal_cycle FROM crm_customer AS a LEFT JOIN crm_contract AS b ON b.customer_id = a.id WHERE a.deleted = 0 AND b.deleted = 0 AND b.audit_status = 20 AND a.owner_user_id IN
#{userId}
AND b.create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY time

SELECT a.owner_user_id, IFNULL( TRUNCATE ( AVG( TIMESTAMPDIFF( DAY, a.create_time, b.order_date )), 1 ), 0 ) AS customer_deal_cycle FROM crm_customer AS a LEFT JOIN crm_contract AS b ON b.customer_id = a.id WHERE a.deleted = 0 AND b.deleted = 0 AND b.audit_status = 20 AND a.owner_user_id IN
#{userId}
AND b.create_time BETWEEN #{times[0],javaType=java.time.LocalDateTime} AND #{times[1],javaType=java.time.LocalDateTime} GROUP BY a.owner_user_id
