<template>
  <s-layout title="推广人排行榜">
    <view class="promoter-rank">
      <view class="nav">
        <view
          class="item"
          :class="state.currentTab === index ? 'active' : ''"
          v-for="(item, index) in tabMaps"
          :key="index"
          @click="switchTap(index)"
        >
          {{ item }}
        </view>
      </view>
      <view class="top-list">
        <template v-for="(item, index) in state.pagination.list" :key="index">
          <view v-if="index < 3" class="item">
            <view class="pictrue">
              <view class="num">
                <text>{{ index + 1 }}</text>
              </view>
              <image class="image" :src="item.avatar"></image>
            </view>
            <view class="people">{{ item.brokerageUserCount }}人</view>
            <view class="nickname">{{ item.nickname }}</view>
          </view>
        </template>
      </view>
    </view>
    <view class="other-list">
      <template v-for="(item, index) in state.pagination.list" :key="index">
        <view class="item" v-if="index > 2">
          <view class="num">{{ index + 1 }}</view>
          <view class="pictrue">
            <image class="image" :src="item.avatar"></image>
          </view>
          <view class="nickname">{{ item.nickname }}</view>
          <view class="people">{{ item.brokerageUserCount }}人</view>
        </view>
      </template>
    </view>
    <uni-load-more
      v-if="state.pagination.total > 0"
      :status="state.loadStatus"
      :content-text="{
        contentdown: '上拉加载更多',
      }"
      @tap="loadMore"
    />
  </s-layout>
</template>

<script setup>
  import sheep from '@/sheep';
  import { onLoad, onReachBottom } from '@dcloudio/uni-app';
  import { reactive } from 'vue';
  import _ from 'lodash-es';
  import BrokerageApi from '@/sheep/api/trade/brokerage';
  import { resetPagination, getWeekTimes, getMonthTimes } from '@/sheep/helper/utils';

  const tabMaps = ['周排行', '月排行'];

  const state = reactive({
    currentTab: 0,
    times: [],
    pagination: {
      list: [],
      total: 0,
      pageNo: 1,
      pageSize: 10,
    },
    loadStatus: '',
  });

  function switchTap(index) {
    if (state.currentTab === index) {
      return;
    }
    state.currentTab = index;
    resetPagination(state.pagination);
    calculateTimes();
    getRankList();
  }

  async function getRankList() {
    state.loadStatus = 'loading';
    // 查询
    const { code, data } = await BrokerageApi.getBrokerageUserRankPageByUserCount({
      pageNo: state.pagination.pageNo,
      pageSize: state.pagination.pageSize,
      'times[0]': state.times[0],
      'times[1]': state.times[1],
    });
    if (code !== 0) {
      return;
    }
    state.pagination.list = _.concat(state.pagination.list, data.list);
    state.pagination.total = data.total;
    state.loadStatus = state.pagination.list.length < state.pagination.total ? 'more' : 'noMore';
  }

  // 加载更多
  function loadMore() {
    if (state.loadStatus === 'noMore') {
      return;
    }
    state.pagination.pageNo++;
    getList();
  }

  // 上拉加载更多
  onReachBottom(() => loadMore());

  function calculateTimes() {
    let times;
    if (state.currentTab === 0) {
      times = getWeekTimes();
    } else {
      times = getMonthTimes();
    }
    state.times = [formatDate(times[0]), formatDate(times[1])];
  }

  onLoad(function () {
    calculateTimes();
    getRankList();
  });

  function formatDate(date) {
    return sheep.$helper.timeFormat(date, 'yyyy-mm-dd hh:MM:ss');
  }
</script>

